import React, { Component } from 'react';
//import Button from '@mui/material/Button';
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome';
import {faCircleMinus, faCirclePlus, faStar} from '@fortawesome/free-solid-svg-icons';

class GetOnlineData extends Component {
    constructor(props){
        super(props);
        this.state = {
            error : null,
            isLoaded : false,
            isUpdated: false,
            posts : []          
        };
    }

    componentDidMount(){

        fetch("http://localhost:9000/user/list/")
        .then( response => response.json())
        .then(
            // handle the result
            (result) => {
                this.setState({
                    isLoaded : true,
                    posts : result
                });
            },

            // Handle error 
            (error) => {
                this.setState({
                    isLoaded: true,
                    error
                })
            },
        )
    }

    createStars(n){
        var Stars = [];
        var i =0;
        for(i =0; i < n; i++){
            Stars.push(<FontAwesomeIcon icon={faStar} fixedWidth/>);
        }
        return Stars;
    }
    incrementStars(name, stars){
        stars++;
        fetch('http://localhost:9000/user/update/' + name, {
            method: 'PATCH',
            body: JSON.stringify(
                {
                    "name" : name,
                    "stars" : stars
                }
            ),
            headers: {
                'Content-Type': 'application/json'
            }
        })
        .then(
            // handle the result
            (response) => {
                console.log(response.status);
                this.setState({
                    isUpdated : true 
                });
            },

            // Handle error 
            (error) => {
                this.setState({
                    isUpdated: true,
                    error
                })
            },
        )
    }

    decrementStars(name, stars){
        stars--;
        return fetch('http://localhost:9000/user/update/' + name, {
            method: 'PATCH',

            body: JSON.stringify(
                {
                    "name" : name,
                    "stars" : stars
                }
            ),
            headers: {
                'Content-Type': 'application/json'
            }
        }).then(res => {
            return res;
        }).catch(err => err);
    }

    render() {
        const {error, isLoaded, posts} = this.state;

        if(error){
            return <div>Error in loading</div>
        }else if (!isLoaded) {
            return <div>Loading ...</div>
        }else{
            return(
                <div>

                    {
                        posts.map(post => (
                            <div>
                            <h2 className="header">{post.name}</h2>



                            <div class="fa fa-3x stars">
                                {this.createStars(post.stars)}
                            </div>
                            <div class='fa fa-2x'>

                            <button onClick={ () => this.incrementStars(post.name, post.stars) } class='plus'><FontAwesomeIcon icon={faCirclePlus} fixedWidth/></button>
                            <button onClick={ () => this.decrementStars(post.name, post.stars) } class='minus'><FontAwesomeIcon icon={faCircleMinus} fixedWidth/></button>

                            </div>
                            <br />
                        </div> 
                        ))
                    }

                </div>
            );
        }

    }
  }

  export default GetOnlineData;
