import React, { Component } from 'react';
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome';
import {faCircleMinus, faCirclePlus, faStar} from '@fortawesome/free-solid-svg-icons';
//import './GetOnlinePosts.css'
// get posts from online api
// it's return a json file
class GetOnlinePosts extends Component {
    constructor(props){
        super(props);
        this.state = {
            error : null,
            isLoaded : false,
            posts : []          
        };
    }

    componentDidMount(){
        // I will use fake api from jsonplaceholder website
        // this return 100 posts 
        fetch("http://localhost:9000/user/list/")
        .then( response => response.json())
        .then(
            // handle the result
            (result) => {
                this.setState({
                    isLoaded : true,
                    posts : result
                });
            },

            // Handle error 
            (error) => {
                this.setState({
                    isLoaded: true,
                    error
                })
            },
        )
    }

    createStars(n){
        var Stars = [];
        var i =0;
        for(i =0; i < n; i++){
            Stars.push(<FontAwesomeIcon icon={faStar} fixedWidth/>);
        }
        return Stars;
    }

    render() {
        const {error, isLoaded, posts} = this.state;

        if(error){
            return <div>Error in loading</div>
        }else if (!isLoaded) {
            return <div>Loading ...</div>
        }else{
            return(
                <div>
                    <ol className="item">
                    {
                        posts.map(post => (
                            <div> 
                                <div>
                                    <p className="title">{post.name}</p>
                                    <p className="body">{post.stars}</p>
                                </div>

                                <div class="fa fa-4x stars">
                                {this.createStars(post.stars)}
                                </div>
                                </div>   
                        ))
                    }
                    </ol>
                </div>
            );
        }

    }
  }

  export default GetOnlinePosts;
