import React, { Component } from 'react';
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome';
import {faCircleMinus, faCirclePlus, faStar} from '@fortawesome/free-solid-svg-icons';

class GetOnlinePosts extends Component {
    constructor(props){
        super(props);
        this.state = {
            error : null,
            isLoaded : false,
            posts : []          
        };
    }

    componentDidMount(){

        fetch("http://localhost:9000/user/list/")
        .then( response => response.json())
        .then(
            // handle the result
            (result) => {
                this.setState({
                    isLoaded : true,
                    posts : result
                });
            },

            // Handle error 
            (error) => {
                this.setState({
                    isLoaded: true,
                    error
                })
            },
        )
    }

    createStars(n){
        var Stars = [];
        var i =0;
        for(i =0; i < n; i++){
            Stars.push(<FontAwesomeIcon icon={faStar} fixedWidth/>);
        }
        return Stars;
    }

    render() {
        const {error, isLoaded, posts} = this.state;

        if(error){
            return <div>Error in loading</div>
        }else if (!isLoaded) {
            return <div>Loading ...</div>
        }else{
            return(
                <div>

                    {
                        posts.map(post => (
                            <div>
                            <h2 className="header">{post.name}</h2>
                            <div class='fa fa-2x'>
                            <FontAwesomeIcon icon={faCirclePlus} fixedWidth/>
                            <FontAwesomeIcon icon={faCircleMinus} fixedWidth/>
                            </div>


                            <div class="fa fa-4x stars">
                                {this.createStars(post.stars)}
                            </div>
                            <br />
                        </div> 
                        ))
                    }

                </div>
            );
        }

    }
  }

  export default GetOnlinePosts;
